import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import './login.css'; // Use same CSS as login

export default function Signup() {
  const [form, setForm] = useState({
    name: '',
    username: '',
    email: '',
    password: '',
    role: 'Employee',
    place: ''
  });

  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/auth/signup', form);
      alert('Signup successful!');
      navigate('/login');
    } catch (error) {
      alert('Signup failed. Please try again.');
      console.error(error);
    }
  };

  const onChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  return (
    <div className="body">
      <div className="login-container">
        <h1 style={{ color: 'midnightblue', fontStyle: 'italic' }}>Sign Up</h1>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="name"
            placeholder="Full Name"
            value={form.name}
            onChange={onChange}
            required
          />
          <input
            type="text"
            name="username"
            placeholder="Username"
            value={form.username}
            onChange={onChange}
            required
          />
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={form.email}
            onChange={onChange}
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={form.password}
            onChange={onChange}
            required
            minLength={8}
          />
          <select name="role" value={form.role} onChange={onChange} required>
            <option value="Employee">Employee</option>
  <option value="Manager">Manager</option>
  <option value="Admin">Admin</option>
</select>

          <input
            type="text"
            name="place"
            placeholder="Location / Place"
            value={form.place}
            onChange={onChange}
            required
          />

          <button type="submit">Sign Up</button>
          <h6 className="signup-link">
            Already have an account? <Link to="/login"><u>Login here</u></Link>
          </h6>
        </form>
      </div>
    </div>
  );
}
