import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function PendingRequests() {
  const [requests, setRequests] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem('token');
    axios.get('http://localhost:5000/api/requests/pending', {
      headers: { Authorization: `Bearer ${token}` }
    }).then(res => setRequests(res.data));
  }, []);

  const handleAction = async (id, status) => {
    const token = localStorage.getItem('token');
    await axios.patch(`http://localhost:5000/api/requests/${id}`, { status }, {
      headers: { Authorization: `Bearer ${token}` }
    });
    alert(`Request ${status}`);
    setRequests(requests.filter(r => r.id !== id));
  };

  return (
    <div>
      {requests.map(r => (
        <div key={r.id}>
          <p>{r.user.username} requested {r.accessType} for {r.software.name}</p>
          <p>Reason: {r.reason}</p>
          <button onClick={() => handleAction(r.id, 'Approved')}>Approve</button>
          <button onClick={() => handleAction(r.id, 'Rejected')}>Reject</button>
        </div>
      ))}
    </div>
  );
}
