import React, { useState } from 'react';
import axios from 'axios';

const CreateSoftware = () => {
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    version: '',
    licenseKey: '',
  });

  const [message, setMessage] = useState('');

  const handleChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      // Replace with your backend API endpoint
      const res = await axios.post('/api/software/create', formData);
      setMessage('Software created successfully!');
      setFormData({ name: '', description: '', version: '', licenseKey: '' });
    } catch (err) {
      setMessage('Error creating software.');
    }
  };

  return (
    <div className="container mt-4" style={{ maxWidth: '600px' }}>
      <h3>Create Software</h3>
      {message && <div className="alert alert-info">{message}</div>}
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label htmlFor="name" className="form-label">
            Software Name
          </label>
          <input
            type="text"
            className="form-control"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
            placeholder="Enter software name"
          />
        </div>

        <div className="mb-3">
          <label htmlFor="description" className="form-label">
            Description
          </label>
          <textarea
            className="form-control"
            id="description"
            name="description"
            rows="3"
            value={formData.description}
            onChange={handleChange}
            placeholder="Enter description"
            required
          />
        </div>

        <div className="mb-3">
          <label htmlFor="version" className="form-label">
            Version
          </label>
          <input
            type="text"
            className="form-control"
            id="version"
            name="version"
            value={formData.version}
            onChange={handleChange}
            required
            placeholder="e.g., 1.0.0"
          />
        </div>

        <div className="mb-3">
          <label htmlFor="licenseKey" className="form-label">
            License Key
          </label>
          <input
            type="text"
            className="form-control"
            id="licenseKey"
            name="licenseKey"
            value={formData.licenseKey}
            onChange={handleChange}
            placeholder="Optional"
          />
        </div>

        <button type="submit" className="btn btn-success">
          Create
        </button>
      </form>
    </div>
  );
};

export default CreateSoftware;
